########  EKF function ########

function EKF(R,pQ,icond)
    p_Q = diag(pQ) # in this code we need only use the diagonal of Process Noise Covariance Matrix, because the off diagonal are zero.

    updated_Xv = []
    updated_Xt = []
    updated_GLC = []
    updated_GLN = []
    updated_LAC = []
    updated_AMM = []
    updated_MAb = []
    updated_P = []

    predicted_Xv = []
    predicted_Xt = []
    predicted_GLC = []
    predicted_GLN = []
    predicted_LAC = []
    predicted_AMM = []
    predicted_MAb = []
    predicted_P = []

    innovation_error=[]
    log_innovation_error=[]
    NIS=[]
    trace_P=[]
    Sk=[]

    Pc=[]

    init = icond #[X0;P0[:]] # Combined initial value vector
    currentTime = 0.0
    prob = ODEProblem(ode_system2!, init, (currentTime,tProcess[1]),p_Q)

    H = [1 0 0 0 0 0 0] # Observation Matrix

    for t_index = eachindex(tProcess)

      tk = tProcess[t_index]
      tspan = (currentTime,tk)

      sol = solve(prob,BS3(), save_everystep = false, save_start = false, u0=init,tspan=(currentTime,tk),p=p_Q)#, Rosenbrock23(), abstol = 1e-10, reltol = 1e-10)    # predicted1 = solve(problem, Rosenbrock23(),  u0=u0_set, p=p, saveat=tgrid,abstol = 1e-10, reltol = 1e-10)

      measurement_k = noise_dataset[:,1][t_index+1]
      newP = reshape(sol(tk)[8:end],7,7); # process covariance matrix
      res1 = (newP*H')
      res2 = H*newP*H'
      dimS = size(res2)
      S = (res2 + ones(dimS[1],dimS[2])*R) #innovation covariance
      K = res1/S # Kalman Gain matrix
      PS = sol(tk)[1:7] #Predicted state
      ie=measurement_k-PS[1] #innovation
      log_ie=log(measurement_k+1)-log(PS[1]+1)
      FS = PS + K*(ie) #Filtered state (Updated state)
      Pfilt = newP-K*H*newP # filtered process covariance matrix (Updated state)

      append!(Sk,S)
      append!(log_innovation_error,log_ie)
      append!(innovation_error,ie)
      append!(trace_P,tr(Pfilt))
      append!(NIS,ie*inv(S)*ie)

    #Predicted state
      append!(predicted_Xv,  PS[1])
      append!(predicted_Xt,  PS[2])
      append!(predicted_GLC, PS[3])
      append!(predicted_GLN, PS[4])
      append!(predicted_LAC, PS[5])
      append!(predicted_AMM, PS[6])
      append!(predicted_MAb, PS[7])
      append!(predicted_P,  newP)
    #Updated state
      append!(updated_Xv,  FS[1])
      append!(updated_Xt,  FS[2])
      append!(updated_GLC, FS[3])
      append!(updated_GLN, FS[4])
      append!(updated_LAC, FS[5])
      append!(updated_AMM, FS[6])
      append!(updated_MAb, FS[7])

      append!(Pc, [diag(Pfilt)])

      # Update initial state variables for the next iteration.
      currentTime = tspan[2]
      init = [FS; Pfilt[:]]
    end
    #return (predicted_Xv, predicted_Xt, predicted_GLC, predicted_GLN, predicted_LAC, predicted_AMM, predicted_MAb, trace_P, NIS, innovation_error,log_innovation_error,Sk,Pc)
    return (updated_Xv, updated_Xt, updated_GLC, updated_GLN, updated_LAC, updated_AMM, updated_MAb, trace_P, NIS, innovation_error,log_innovation_error,Sk,Pc)
end




##################### Checking consistency #################


function test_ekf(r,q,icond)
    ekf=EKF(r,q,icond)

    trace_P=ekf[8]
    norm_trace_P=sqrt(sum(trace_P.^2))

    innovation_error=ekf[10]
    rmse= sqrt(sum(innovation_error.^2)/length(innovation_error))
    println("RMSE(innovation_error): ",rmse)

    log_innovation_error=ekf[11]
    rmsle= sqrt(sum(log_innovation_error.^2)/length(log_innovation_error))
    println("RMSLE(innovation_error): ",rmsle)

    Sk=ekf[12]

    NIS=ekf[9]
    mean_NIS=mean(NIS)
    println("mean(NIS): ",mean_NIS)
    var_NIS=var(NIS)
    println("var(NIS): ",var_NIS)

    N=length(NIS)
    # https://www.chisquaretable.net/
    # https://www.itl.nist.gov/div898/handbook/eda/section3/eda3674.htm
    # r1=6.262 #5.629 CI from table using N=15 size of noise dt
    # r2=27.488 #26.119
    r1=5.629 # CI from table using N=14 size of noise dt
    r2=26.119



    println("\n====== Innovation magnitude bound test ======")
    println("What is the percentege of innovation errors that lie within the ±2sqrt(Sk)? ", (N-(sum(-2*sqrt.(Sk).>innovation_error)+sum(innovation_error.>2*sqrt.(Sk))))/N )

    # println("What is the percentege of innovation errors lie within the ±2sqrt(Sk) ", (N-(sum(-2*sqrt.(Sk).>innovation_error)+sum(innovation_error.>2*sqrt.(Sk))))/N )

