    ppp=plot(NIS,label="NIS")
    plot!(repeat([mean_NIS], N), label="mean(NIS) ~ 1" )
    plot!(repeat([r1/N], N), label="r1/N" )
    plot!(repeat([r2/N], N), label="r2/N" )
    # plot!(repeat([r2one/N], N), label="r2one/N" )
    display(ppp)

    # plot Innovation magnitude bound test
    ppp=plot(innovation_error,label="Innovation error")
    plot!(2*sqrt.(Sk), label="+2sqrt(Sk)" )
    plot!(-2*sqrt.(Sk), label="-2sqrt(Sk)" )
    display(ppp)

    EKF_estimations=noise_dataset[:,:]
    EKF_estimations[2:end,1]=ekf[1][:,:]
    EKF_estimations[2:end,2]=ekf[2][:,:]
    EKF_estimations[2:end,3]=ekf[3][:,:]
    EKF_estimations[2:end,4]=ekf[4][:,:]
    EKF_estimations[2:end,5]=ekf[5][:,:]
    EKF_estimations[2:end,6]=ekf[6][:,:]
    EKF_estimations[2:end,7]=ekf[7][:,:]

    gr();
    plots=Plots.scatter(tgrid,noise_dataset, label = false, ylabel=["[Xv]" "[Xd]"  "[GLC]" "[GLN]" "[LAC]" "[AMM]" "[mAb]"],layout=(3,3) )
    plot!(tProcess_total[2:end],EKF_estimations[2:end,:],color=[:red :red :red :red :red :red :red], lw=1.5, label = false, ylabel=["[Xv]" "[Xd]"  "[GLC]" "[GLN]" "[LAC]" "[AMM]" "[mAb]"], layout=(3,3))
    display(plots)

    Pc=ekf[13]

    return ekf
